import React, { useState, useRef, useEffect } from 'react';
import Slider from 'react-slick';
import './ChatInterface.css';
import {sendMessage} from "../utils/client"; // Import CSS file for styling



const ChatInterface = () => {
  const [inputMessage, setInputMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const sliderRef = useRef(null);

  useEffect(() => {
    // Scroll to bottom of the chat history when chat history updates
    if (sliderRef.current) {
      sliderRef.current.slickGoTo(chatHistory.length);
    }
  }, [chatHistory]);

  const handleInputChange = (e) => {
    setInputMessage(e.target.value);
  };

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
        setChatHistory([...chatHistory, {sender: 'user', content: inputMessage}])
      // Send user message to Lex
      const response = await sendMessage(inputMessage);
      const messages = response.messages;
      const newChatHistory = [
          ...chatHistory,
          ...messages.map(message => ({
              sender: 'bot',
              content: message.content,
              responseCard: message.imageResponseCard,
              title: message.title
          }))
      ]
      // Update chat history with user message and Lex response
      setChatHistory(newChatHistory);
      setInputMessage('');
    }
  };

  return (
    <div className="chat-interface">
      <div className="header-box">
        <h1>HAL BOT</h1>
      </div>
      <Slider
        ref={sliderRef}
        dots={false}
        arrows={false}
        infinite={false}
        speed={500}
        slidesToShow={1}
        slidesToScroll={1}
        vertical={true}
        className="chat-history"
      >
        {chatHistory.map((message, index) => (
          <div key={index} className="message">
              {message.sender === 'bot' && <p className="bot-message">Bot: {message.bot}</p>}
              {message.sender === 'user' && <p className="user-message">User: {message.user}</p>}
          </div>
        ))}
      </Slider>
      <div className="message-input">
        <input
          type="text"
          value={inputMessage}
          onChange={handleInputChange}
          placeholder="Type your message..."
          className="input-field"
        />
        <button onClick={handleSendMessage} className="send-button">Send</button>
      </div>
    </div>
  );
};

export default ChatInterface;
